import React from 'react';
import axios from 'axios';

function ResumeForm({ formData, setFormData }) {
  const updateField = (field, value) => {
    setFormData({ ...formData, [field]: value });
  };

  const addEducation = () => {
    const newEducation = [...formData.education, { institution: '', degree: '', year: '' }];
    setFormData({ ...formData, education: newEducation });
  };


const handleSubmit = async () => {
  try {
    const response = await axios.post('http://localhost:8080/resume', formData);
    alert('Resume saved successfully!');
  } catch (error) {
    console.error('Error saving resume:', error);
    alert('Failed to save resume.');
  }
};


  const addExperience = () => {
    const newExperience = [...formData.experience, { company: '', role: '', duration: '' }];
    setFormData({ ...formData, experience: newExperience });
  };

  const updateEducation = (index, field, value) => {
    const updated = [...formData.education];
    updated[index][field] = value;
    setFormData({ ...formData, education: updated });
  };

  const updateExperience = (index, field, value) => {
    const updated = [...formData.experience];
    updated[index][field] = value;
    setFormData({ ...formData, experience: updated });
  };

  const addSkill = (e) => {
    if (e.key === 'Enter' && e.target.value.trim()) {
      const newSkills = [...formData.skills, e.target.value.trim()];
      setFormData({ ...formData, skills: newSkills });
      e.target.value = '';
    }
  };

  return (
    <div className="p-4 border rounded shadow">
      <h2 className="text-xl font-bold mb-2">Personal Information</h2>
      <input type="text" placeholder="Full Name" className="input" onChange={(e) => updateField('name', e.target.value)} />
      <input type="email" placeholder="Email" className="input" onChange={(e) => updateField('email', e.target.value)} />
      <input type="text" placeholder="Phone" className="input" onChange={(e) => updateField('phone', e.target.value)} />

      <h2 className="text-xl font-bold mt-4 mb-2">Education</h2>
      {formData.education.map((edu, i) => (
        <div key={i} className="mb-2">
          <input type="text" placeholder="Institution" className="input" onChange={(e) => updateEducation(i, 'institution', e.target.value)} />
          <input type="text" placeholder="Degree" className="input" onChange={(e) => updateEducation(i, 'degree', e.target.value)} />
          <input type="text" placeholder="Year" className="input" onChange={(e) => updateEducation(i, 'year', e.target.value)} />
        </div>
      ))}
      <button onClick={addEducation} className="btn">+ Add Education</button>

      <button onClick={handleSubmit} className="btn mt-4 bg-green-600 hover:bg-green-700 text-white">Save Resume</button>



      <h2 className="text-xl font-bold mt-4 mb-2">Experience</h2>
      {formData.experience.map((exp, i) => (
        <div key={i} className="mb-2">
          <input type="text" placeholder="Company" className="input" onChange={(e) => updateExperience(i, 'company', e.target.value)} />
          <input type="text" placeholder="Role" className="input" onChange={(e) => updateExperience(i, 'role', e.target.value)} />
          <input type="text" placeholder="Duration" className="input" onChange={(e) => updateExperience(i, 'duration', e.target.value)} />
        </div>
      ))}
      <button onClick={addExperience} className="btn">+ Add Experience</button>


      
      <h2 className="text-xl font-bold mt-4 mb-2">Skills</h2>
      <input type="text" placeholder="Type skill and press Enter" className="input" onKeyDown={addSkill} />
      <div className="flex flex-wrap gap-2 mt-2">
        {formData.skills.map((skill, i) => (
          <span key={i} className="px-2 py-1 bg-gray-200 rounded-full text-sm">{skill}</span>
        ))}
      </div>
    </div>
  );
}

export default ResumeForm;
