import React, { useRef } from 'react';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

const ResumePreview = ({ data }) => {
  const previewRef = useRef();

  const handleDownload = () => {
    html2canvas(previewRef.current).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (canvas.height * pdfWidth) / canvas.width;
      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      pdf.save('resume.pdf');
    });
  };

  return (
    <div className="p-4 border rounded shadow bg-white">
      <div ref={previewRef} className="p-4">
        <h2 className="text-xl font-bold">{data.name}</h2>
        <p>Email: {data.email}</p>
        <p>Phone: {data.phone}</p>

        <h3 className="mt-4 font-bold">Education</h3>
        {data.education.map((edu, i) => (
          <p key={i}>{edu.degree} at {edu.institution} ({edu.year})</p>
        ))}

        <h3 className="mt-4 font-bold">Experience</h3>
        {data.experience.map((exp, i) => (
          <p key={i}>{exp.role} at {exp.company} ({exp.duration})</p>
        ))}

        <h3 className="mt-4 font-bold">Skills</h3>
        <ul className="list-disc list-inside">
          {data.skills.map((skill, i) => (
            <li key={i}>{skill}</li>
          ))}
        </ul>
      </div>
      <button onClick={handleDownload} className="btn mt-4">Download as PDF</button>
    </div>
  );
};

export default ResumePreview;
