import React, { useState } from 'react';

const ResumeForm = ({ onFormChange }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    education: [{ institution: '', degree: '', year: '' }],
    experience: [{ company: '', role: '', duration: '' }],
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => {
      const updated = { ...prev, [name]: value };
      onFormChange(updated);
      return updated;
    });
  };

  const handleNestedChange = (index, field, value, type) => {
    const updatedArray = [...formData[type]];
    updatedArray[index][field] = value;

    setFormData((prev) => {
      const updated = { ...prev, [type]: updatedArray };
      downloadge(updated);
      return updated;
    });
  };

  const addSection = (type) => {
    setFormData((prev) => {
      const updated = {
        ...prev,
        [type]: [...prev[type], { institution: '', degree: '', year: '' }]
      };
      onFormChange(updated);
      return updated;
    });
  };

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Personal Information</h2>
      <input name="name" placeholder="Full Name" className="input" onChange={handleChange} />
      <input name="email" placeholder="Email" className="input" onChange={handleChange} />
      <input name="phone" placeholder="Phone" className="input" onChange={handleChange} />

      <h2 className="text-xl font-bold mt-6 mb-4">Education</h2>
      {formData.education.map((edu, index) => (
        <div key={index} className="mb-4">
          <input
            placeholder="Institution"
            className="input"
            value={edu.institution}
            onChange={(e) => handleNestedChange(index, 'institution', e.target.value, 'education')}
          />
          <input
            placeholder="Degree"
            className="input"
            value={edu.degree}
            onChange={(e) => handleNestedChange(index, 'degree', e.target.value, 'education')}
          />
          <input
            placeholder="Year"
            className="input"
            value={edu.year}
            onChange={(e) => handleNestedChange(index, 'year', e.target.value, 'education')}
          />
        </div>
      ))}
      <button className="btn" onClick={() => addSection('education')}>+ Add Education</button>

      <h2 className="text-xl font-bold mt-6 mb-4">Experience</h2>
      {formData.experience.map((exp, index) => (
        <div key={index} className="mb-4">
          <input
            placeholder="Company"
            className="input"
            value={exp.company}
            onChange={(e) => handleNestedChange(index, 'company', e.target.value, 'experience')}
          />
          <input
            placeholder="Role"
            className="input"
            value={exp.role}
            onChange={(e) => handleNestedChange(index, 'role', e.target.value, 'experience')}
          />
          <input
            placeholder="Duration"
            className="input"
            value={exp.duration}
            onChange={(e) => handleNestedChange(index, 'duration', e.target.value, 'experience')}
          />
        </div>
      ))}
      <button className="btn" onClick={() => addSection('experience')}>+ Add Experience</button>
    </div>
  );
};

export default ResumeForm;
